import camel_case as ccimport nose.tools as ntdef test_all_lower():    result = cc.camel_case_to_underscore("test")    nt.eq_("test", result)def test_one_capital():    result = cc.camel_case_to_underscore("helloWorld")    nt.eq_("hello_world", result)def test_start_with_capital():    result = cc.camel_case_to_underscore("HelloWorld")    nt.eq_("hello_world", result)def test_capital_acronym():    result = cc.camel_case_to_underscore("parseHTMLDocument")    nt.eq_("parse_html_document", result)def test_ends_with_capital_acronym():    result = cc.camel_case_to_underscore("parseHTML")    nt.eq_("parse_html", result)